import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, Image, Pressable , TouchableOpacity , ScrollView} from 'react-native';
import {useState} from 'react';
import { useFonts } from 'expo-font';
import { useNavigation } from '@react-navigation/native';
import Card from './Card.js';
import Avatar from './Avatar.js'
import { MyButton } from "./MyButton.js";


const ruler = require("../assets/ruler&pen.svg");
const add = require("../assets/add-circle.svg");
const avatar1 = require("../assets/images/avatar1.png"); 
const avatar2 = require("../assets/images/avatar2.png");
const avatar3 = require("../assets/images/avatar3.png"); 
const avatar4 = require("../assets/images/avatar4.png");
const avatar5 = require("../assets/images/avatar5.png"); 
const avatar6 = require("../assets/images/avatar6.png");
const avatar7 = require("../assets/images/avatar7.png"); 
const avatar8 = require("../assets/images/avatar8.png");
const avatar9 = require("../assets/images/avatar9.png"); 
const avatar10 = require("../assets/images/avatar10.png");
const avatar11 = require("../assets/images/avatar11.png"); 
const avatar12 = require("../assets/images/avatar12.png");
export default function Avatars({ route }) {
  const navigation = useNavigation()
  const [loaded] = useFonts({
    'Poppins': require('../assets/fonts/Poppins-Regular.ttf'),
  });

  if (!loaded) {
    return null;
  }
  const username = route.params.username;
  const token = route.params.token;
  const defaultStyle = {
    width: "100px",
    height: "90px",
    borderRadius: "20px",
    linearGradient: "rgba(0,0,0,0)",
  };
  const [viewStyle, setViewStyle] = useState(defaultStyle);
  const changeViewStyle = () => {
    const updatedStyle = {
      ...defaultStyle,
      width: "100px",
    height: "50px",
    borderRadius: "20px",
    linearGradient: "rgba(0,0,0,0)",
    };
    setViewStyle(updatedStyle);
  };
  const avatars = [
    avatar1,
    avatar2,
    avatar3,
    avatar4,
    avatar5,
    avatar6,
    avatar7,
    avatar8,
    avatar9,
    avatar10,
    avatar11,
    avatar12,
  ];
  const handleImage = () => {
    navigation.navigate('Welcome', {username : username, token: token})
  };
	return ( 
        <View style={styles.container}>
			<Text style={styles.title}>Choisissez un Avatar</Text>
            <ScrollView>
                <View style={styles.containerAvatar}>

                {avatars.map((avatar) => (
                    <Avatar source={avatar} onPress={handleImage} key={avatar} style={defaultStyle} />
                ))}
                </View>
            </ScrollView>
            <MyButton label="Continuer" primary={true} onPress={handleImage} />   
        </View>



	);
}

const styles = StyleSheet.create({
  container: {
    height: "80%",
    top: 64,
    alignItems: 'center',
    justifyContent: 'space-around',
    },
  containerAvatar: {
    width: "100%",
    height: "100%",
    display: "grid",
    gridTemplateColumns: "repeat(3,1fr)",
    // gridTemplateRows: "2fr",
    padding: "15px",
    gap: "15px",
    // marginBottom: "-10px",
  },
  title: {
    color: '#ffffff',
    fontSize: 28,
    fontFamily: 'Poppins'
  },
})
