import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, Image, Pressable , TouchableOpacity } from 'react-native';
import {useState} from 'react';
import { useFonts } from 'expo-font';
import { useNavigation } from '@react-navigation/native';
import Card from './Card.js';
import Avatar from './Avatar.js'

const ruler = require("../assets/ruler&pen.svg");
const add = require("../assets/add-circle.svg");
const avatar1 = require("../assets/images/avatar1.png"); 
const avatar2 = require("../assets/images/avatar2.png");
export default function Avatars({ route }) {
  const navigation = useNavigation()
  const [loaded] = useFonts({
    'Poppins': require('../assets/fonts/Poppins-Regular.ttf'),
  });

  if (!loaded) {
    return null;
  }
  const username = route.params.username;
  const token = route.params.token;
	return (
		<View style={styles.container}>
			<Text style={styles.title}>Choisissez un Avatar</Text>
            <Avatar source={avatar1} onPress={()=>navigation.navigate('Welcome', {username : username, token: token})}/>
            <Avatar source={avatar2} onPress={()=>navigation.navigate('Welcome', {username : username, token: token})}/>
		</View>
	);
}

const styles = StyleSheet.create({
  container: {
    height: "80%",
    top: 64,
    alignItems: 'center',
    justifyContent: 'space-around',
  },
  title: {
    color: '#ffffff',
    fontSize: 28,
    fontFamily: 'Poppins'
  },
})
