import {
    ImageBackground,
    ScrollView,
    StyleSheet,
    TouchableOpacity,
    View,
  } from "react-native-web";
  import { LinearGradient } from "expo-linear-gradient";
  //
  // const LinearGradient = require("react-native-linear-gradient");
  const image1 = require("../assets/images/avatar1.png");

  export default function Avatar({ source, onPress , clicked}) {
    return (
      <TouchableOpacity onPress={onPress}>
        <LinearGradient
          style={
            clicked == true
              ? [styles.middleBoxItem, { borderWidth: solid,borderColor: 'black' }]
              : styles.middleBoxItem
          }
          colors={["purple", "#000000"]}
          locations={[0, 1]}
        >
          <ImageBackground
            source={source}
            style={styles.middleBoxItem}
          ></ImageBackground>
        </LinearGradient>
      </TouchableOpacity>
    );
  };




const styles = StyleSheet.create({
