import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, Image, Pressable , TouchableOpacity , ScrollView} from 'react-native';
import {useState} from 'react';
import { useFonts } from 'expo-font';
import { useNavigation } from '@react-navigation/native';
import Card from './Card.js';
import Avatar from './Avatar.js'

const ruler = require("../assets/ruler&pen.svg");
const add = require("../assets/add-circle.svg");
const avatar1 = require("../assets/images/avatar1.png"); 
const avatar2 = require("../assets/images/avatar2.png");
const avatar3 = require("../assets/images/avatar3.png"); 
const avatar4 = require("../assets/images/avatar4.png");
const avatar5 = require("../assets/images/avatar5.png"); 
const avatar6 = require("../assets/images/avatar6.png");
const avatar7 = require("../assets/images/avatar7.png"); 
const avatar8 = require("../assets/images/avatar8.png");
const avatar9 = require("../assets/images/avatar9.png"); 
const avatar10 = require("../assets/images/avatar10.png");
const avatar11 = require("../assets/images/avatar11.png"); 
const avatar12 = require("../assets/images/avatar12.png");
export default function Avatars({ route }) {
  const navigation = useNavigation()
  const [loaded] = useFonts({
    'Poppins': require('../assets/fonts/Poppins-Regular.ttf'),
  });

  if (!loaded) {
    return null;
  }
  const username = route.params.username;
  const token = route.params.token;
	return (
		<View style={styles.container}>
			<Text style={styles.title}>Choisissez un Avatar</Text>
            <ScrollView>
            <Avatar source={avatar1} onPress={()=>navigation.navigate('Welcome', {username : username, token: token})}/>
            <Avatar source={avatar2} onPress={()=>navigation.navigate('Welcome', {username : username, token: token})}/>
            <Avatar source={avatar3} onPress={()=>navigation.navigate('Welcome', {username : username, token: token})}/>
            <Avatar source={avatar4} onPress={()=>navigation.navigate('Welcome', {username : username, token: token})}/>
            </ScrollView>
            {/* <Avatar source={avatar5} onPress={()=>navigation.navigate('Welcome', {username : username, token: token})}/>
            <Avatar source={avatar6} onPress={()=>navigation.navigate('Welcome', {username : username, token: token})}/>
            <Avatar source={avatar7} onPress={()=>navigation.navigate('Welcome', {username : username, token: token})}/>
            <Avatar source={avatar8} onPress={()=>navigation.navigate('Welcome', {username : username, token: token})}/>
            <Avatar source={avatar9} onPress={()=>navigation.navigate('Welcome', {username : username, token: token})}/>
            <Avatar source={avatar10} onPress={()=>navigation.navigate('Welcome', {username : username, token: token})}/>
            <Avatar source={avatar11} onPress={()=>navigation.navigate('Welcome', {username : username, token: token})}/>
            <Avatar source={avatar12} onPress={()=>navigation.navigate('Welcome', {username : username, token: token})}/> */}
		</View>
	);
}

const styles = StyleSheet.create({
  container: {
    height: "80%",
    top: 64,
    alignItems: 'center',
    justifyContent: 'space-around',
  },
  title: {
    color: '#ffffff',
    fontSize: 28,
    fontFamily: 'Poppins'
  },
})
