import { ScrollView, StyleSheet, Text, View } from "react-native-web";
import PartieMessage from "./PartieMessage";
export default function FooterMessageField() {
  const messages = [
    { id: "juan Pablo", text: "probando id 1" },
    {
      id: "miguel angel",
      text: "probando id 2",
